    function openSeoPopup(id) {
      const data = seoPopupData[id];
      if (!data) return;

      document.getElementById('seoPopupTitle').textContent = data.title;
      document.getElementById('seoPopupProblem').textContent = data.problem;

      // Strategy list
      const stratList = document.getElementById('seoPopupStrategy');
      stratList.innerHTML = "";
      data.strategy.forEach(s => {
        const li = document.createElement('li');
        li.textContent = s;
        stratList.appendChild(li);
      });

      // Results
      const resultDiv = document.getElementById('seoPopupResults');
      resultDiv.innerHTML = "";
      data.results.forEach(r => {
        const box = document.createElement('div');
        box.className = "seo-result-box";
        box.innerHTML = `<strong>${r.value}</strong><span>${r.label}</span>`;
        resultDiv.appendChild(box);
      });

      // document.getElementById('seoPopupImage').src = data.image;

      // Show popup
      document.getElementById('seoPopupOverlay').style.display = 'block';
      document.getElementById('seoPopupResults').style.display = 'block';
    }

    function closeSeoPopup() {
      document.getElementById('seoPopupOverlay').style.display = 'none';
