<script>
  function toggleAccordion(header) {
    const allItems = document.querySelectorAll('.accordion-item');

    allItems.forEach(item => {
      item.querySelector('.accordion-body').classList.remove('show');
      item.querySelector('.accordion-header').classList.remove('open');
    });

    const body = header.nextElementSibling;
    const isAlreadyOpen = body.classList.contains('show');

    if (!isAlreadyOpen) {
      body.classList.add('show');
      header.classList.add('open');

      // Scroll to the clicked accordion item
      header.parentElement.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  }
</script>
